INSERT INTO `ca_estado_pedido` VALUES (1, 'En espera de Revision.', 'Su orden de compra ha sido recibida. La empresa verificará su pedido. Se corrobará la existencia en stock de los productos solicitados, asi como también se le informarán si debe abonar gastos de envio.<br><br>\r\n\r\nNO debe abonar hasta que el estado de su pedido sea "Confirmado. A la espera del pago." ');
