INSERT INTO `ca_forma_pago` VALUES (1, 'Retira por el local', 'Ud. puede retirar el pedido por el local una vez que el pedido se encuentré CONFIRMADO. Recuerde llevar su número de pedido para poder retirarlo.', 2);
INSERT INTO `ca_forma_pago` VALUES (2, 'Contrareembolso', 'IMPORTANTE: Recuerde que el envío con cobro contrareembolso sólo es válido dentro de la República Argentina y tiene un costo adicional de 5,0% sobre el total de la orden. El mismo le será cobrado en el momento de la entrega. ', 1);
INSERT INTO `ca_forma_pago` VALUES (3, 'CuentaDigital', '<p>IMPORTANTE: El monto m&iacute;nimo para operar con Dinero Mail es de $5. Recuerde que al finalizar la compra deber&aacute; ingresar a Dinero Mail y realizar el pago correspondiente para recibir su pedido. El plazo habitual de salida del dep&oacute;sito de cada producto comenzar&aacute; a correr una vez efectivizado el pago de su compra.</p>', 2);
INSERT INTO `ca_forma_pago` VALUES (4, 'Venta telefónica', '<p>Ud. puede abonar con tarjeta de cr&eacute;dito VISA &oacute; Mastercard v&iacute;a telef&oacute;nica con una de nuestras operadoras.<br /><br />Nuestro personal se comunicar&aacute; con usted en el horario que indic&oacute; previamente, cuando su orden de compra sea confirmada.</p>', 1);
INSERT INTO `ca_forma_pago` VALUES (5, 'Depósito bancario o Transferencia bancaria', '<p>Ud. puede abonar sus servicios por Dep&oacute;sito o Transferencia Bancaria:<br /><br /><img src="imagen/logos/ic-fp-bbva-banco-frances.gif" alt="" width="138" height="26" /> <br /><br /><strong>Datos para dep&oacute;sitos y/o transferencia:</strong><br />Entidad Bancaria: <strong>BBVA Banco Franc&eacute;s</strong><br />Titular de Cuenta: <strong>Dattatec.com S.R.L</strong><br />Tipo de cuenta: <strong>Cuenta Corriente en pesos</strong><br />N&uacute;mero de cuenta: <strong>282-2907/2</strong><br />CBU: <strong>01702820 20000000290724</strong><br />CUIT: <strong>30-71017365-2</strong></p>', 1);
INSERT INTO `ca_forma_pago` VALUES (6, 'Pago Fàcil', '<img src="imagen/logos/ic-fp-pagofacil.gif" width="49" height="48"><BR><BR>\r\nPara su mayor comodidad, Ud. puede pagar a trav&eacute;s de Pago F&aacute;cil. Simplemente   deber&aacute; dirigirse a cualquiera de las sucursales y seguir estos pasos:<BR>\r\n  <BR>\r\n  1-   S&oacute;lo mencione a Dattatec.com o el siguiente c&oacute;digo: 900600120005<BR>\r\n  2- Indique   el importe a pagar seg&uacute;n su plan o servicio.<BR>\r\n3- Indique su n&uacute;mero de cliente.   Podr&aacute; encontrar el mismo en su Mesa de Ayuda sobre la barra de men&uacute; de la   izquierda.', 1);
INSERT INTO `ca_forma_pago` VALUES (7, 'Rapi Pago', '<img src="imagen/logos/ic-fp-rapipago.gif" width="90" height="26"><BR>\r\n<BR>\r\nPara su mayor comodidad, Ud. puede pagar a trav&eacute;s de RapiPago. Simplemente   deber&aacute; dirigirse a cualquiera de las sucursales y seguir estos pasos:<BR>\r\n<BR>\r\n1-   Mencione a Dattatec.com o el siguiente c&oacute;digo: 560<BR>\r\n2- Indique el importe a   pagar seg&uacute;n su plan o servicio.<BR>\r\n3- Indique su n&uacute;mero de cliente. Podr&aacute;   encontrar el mismo en su Mesa de Ayuda sobre la barra de men&uacute; de la izquierda.', 1);
