INSERT INTO `carpeta_idioma_dato` VALUES (34, 2, 'Ideal Itinerary', '', '<p class="TituloSeccion">DAY # 01 - ARRIVAL TO IGUAZU FALLS</p>\r\n<p>+ Early flight Buenos Aires to Iguazu Falls (1 hs 40 min)</p>\r\n<p>+ Transfer IN Airport to Hotel</p>\r\n<p>+ Iguazu Brazilian Side Falls Tour ( Half Day)</p>\r\n<p>* Visa Required for US Citizens</p>\r\n<p class="TituloSeccion">DAY # 02 - IGUAZU FALLS</p>\r\n<p>+ Full Day Argentinean Side Falls Tour</p>\r\n<p>+ Great Adventure Tour</p>\r\n<p class="TituloSeccion">DAY # 03 - IGUAZU FOREST</p>\r\n<p>+ Iguazu Forest Adventure Tour</p>\r\n<p>+ Transfer OUT Hotel to Airport</p>\r\n<p>+ Flight Iguazu Falls to Buenos Aires (1 hs 40 min)</p>', 1);
