INSERT INTO `seccion_idioma_dato` VALUES (66, 1, 'Esplendor Buenos Aires', 'esplendor-buenos-aires', 'El primer hotel inagurado en Buenos Aires renace como un nuevo icono porteño. El Hotel Phoenix re abre sus puertas como el ESPLENDOR DE BUENOS AIRES. Combinando la clásica fachada de un edificio histórico, con interiores remodelado íntegramente con diseño de vanguardia...', '&lt;p&gt;El primer hotel inagurado en Buenos Aires renace como un nuevo icono porte&amp;ntilde;o. El Hotel Phoenix re abre sus puertas como el ESPLENDOR DE BUENOS AIRES. Combinando la cl&amp;aacute;sica fachada de un edificio hist&amp;oacute;rico, con interiores remodelado &amp;iacute;ntegramente con dise&amp;ntilde;o de vanguardia y cada objeto dise&amp;ntilde;ado especialmente para convertirlo en un espacio &amp;uacute;nico. El Esplendor Buenos Aires es la mejor propuesta hotelera para el viajante que busca la combinaci&amp;oacute;n perfecta entre servicio, confort y dise&amp;ntilde;o.&lt;/p&gt;', '', '', 1);
INSERT INTO `seccion_idioma_dato` VALUES (66, 2, 'Esplendor Buenos Aires', 'esplendor-buenos-aires', 'The legendary \\"Hotel Phoenix\\" reopened to become a new symbol of Buenos Aires. Now called \\"Esplendor Buenos Aires\\", the hotel combines the classic façade of the building -declared historic patrimony of the city- with totally remodelled interiors of avant-garde design.', '&lt;p&gt;The legendary &quot;Hotel Phoenix&quot; reopened to become a new symbol of Buenos Aires. Now called &quot;Esplendor Buenos Aires&quot;, the hotel combines the classic fa&amp;ccedil;ade of the building -declared historic patrimony of the city- with totally remodelled interiors of avant-garde design.&lt;/p&gt;', '', '', 1);
